#pragma config(Hubs,  S1, HTMotor,  HTServo,  none,     none)
#pragma config(Sensor, S4,     sensorSonar,         sensorSONAR)
#pragma config(Motor,  mtr_S1_C1_1,     leftMotor,     tmotorNormal, PIDControl, encoder)
#pragma config(Motor,  mtr_S1_C1_2,     rightMotor,    tmotorNormal, openLoop, reversed)
#pragma config(Servo,  srvo_S1_C2_1,    servoRight,           tServoStandard)
#pragma config(Servo,  srvo_S1_C2_2,    servoLeft,            tServoStandard)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

#include "JoystickDriver.c"

//TJoystick joystick;

void motorControlProportional()
{
  int nLeftMotor;
  int nRightMotor;

  nLeftMotor = joystick.joy1_y1;
  nRightMotor = joystick.joy1_y2;

  motor[rightMotor] = nRightMotor; // use power settings from Y-axis
  motor[leftMotor]  = nLeftMotor;
}

void buttonControl()
{
  if (joystick.joy1_Buttons == 02)
  {
     servo[servoRight] = 120;
     servo[servoLeft] = 76;
  }
  if (joystick.joy1_Buttons == 01)
  {
     servo[servoRight] = 46;
     servo[servoLeft] = 150;
  }
  if (joystick.joy1_Buttons == 04)
  {
     servo[servoRight] = 165;
     servo[servoLeft] = 21;
  }
  if (joystick.joy1_Buttons == 08)
  {
     servo[servoRight] = 196;
     servo[servoLeft] = 0;
  }
  if (joystick.joy1_Buttons == 16)
  {
     servo[servoRight] = 6;
     servo[servoLeft] = 190;
  }
}

task main()
{
  while (true)
  {
    getJoystickSettings(joystick);
    motorControlProportional();
    buttonControl();
  }
}
