#pragma config(Hubs,  S1, HTMotor,  none,     none,     none)
#pragma config(Sensor, S1,     ,                    sensorI2CMuxController)
#pragma config(Motor,  mtr_S1_C1_1,     leftMotor,          tmotorNormal, openLoop, reversed)
#pragma config(Motor,  mtr_S1_C1_2,     rightMotor,         tmotorNormal, openLoop)
//*!!Code automatically generated by 'ROBOTC' configuration wizard               !!*//

/************************************************************
 * Simple Autonomous Mode Two Motor (left/rightMotor) Drive Code
 ************************************************************
 *
 * This program simply drives the robot straight for 2 seconds.
 *
 ************************************************************
 * Hardware Configuration
 * ----------------------
 * A single HiTechnic Motor Controller with two motors should be
 * connected to Port 1 of the NXT
 * 
 * Motors
 * ------
 * leftMotor  - Connected to Motor 1 of Motor Controller: Controls left motor
 * rightMotor - Connected to Motor 2 of Motor Controller: Controls right motor
 ************************************************************
 */

// This is necessary so that waitForStart() communication from the
// Samantha Field Control is received properly.
#include "JoystickDriver.c"

// Setup the robot before we start
void initializeRobot() {
    // Turn off both motors just in case
    motor[leftMotor] = 0;
    motor[rightMotor] = 0;
}

// The 'main' program
task main() {
    initializeRobot();

    // wait for start from Field Control
    waitForStart();

    // Turn on both motors at the same power (drive straight)
    motor[leftMotor] = 40;
    motor[rightMotor] = 40;

    // Wait for 2 seconds
    wait1Msec(2 * 1000);

    // Done moving, turn off the motors
    motor[leftMotor] = 0;
    motor[rightMotor] = 0;
}
